var env = process.env;

/**
 * The user's home directory
 * 
 * @type {string}
 */
var userHome = null;

/**
 * The `env` variables to check
 * 
 * @type {Array}
 */
var paths = [
  env.HOME,
  env.HOMEPATH,
  env.USERPROFILE
];

/**
 * Check if variable is defined
 * 
 * @param  {string} variable  The variable to check
 * @return {bool}             True if defined; false if not.
 */
function def(variable) {
  if (typeof variable === 'undefined') return false;
  return true;
}

/**
 * Loop through paths to find user home directory
 * 
 * @return {string} The path to the user's home directory or null
 */
function getUserHomeDir() {
  paths.forEach(function(path) {
    if (def(path)) userHome = path;
  });
  return userHome;
}

module.exports = getUserHomeDir();